import React ,{Component} from 'react';
import logo from './logo.svg';
import {Redirect, BrowserRouter, Route } from 'react-router-dom';
import './App.css';
import HomePage from './pages/HomePage.js'
import Add_User from './pages/Add_User.js'
import Login from './pages/Login'
import Profile from './pages/Profile'
import Search from './pages/Search'
import My_Account from './pages/My_Account'
import Potential_Card from './components/Potential_card'
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
  state ={
      redirect1: false,
      redirect2: false,
      redirect3: false,
      redirect4: false,
      redirect5: false,
      redirect6: false,
      redirect7: false,

  }

  setRedirect1 = () => {
    this.setState({
      redirect1: true
    })
  }
  setRedirect2 = () => {
    this.setState({
      redirect2: true
    })
  }
  setRedirect3 = () => {
    this.setState({
      redirect3: true
    })
  }
  setRedirect4 = () => {
    this.setState({
      redirect4: true
    })
  }
  setRedirect5 = () => {
    this.setState({
      redirect5: true
    })
  }
  setRedirect6 = () => {
    this.setState({
      redirect6: true
    })
  }
  setRedirect7 = () => {
    this.setState({
      redirect7: true
    })
  }

  renderRedirect1 = () => {
    if (this.state.redirect1) {
      return <Redirect to='/PC/:daterID/messages' />
    }
  }
  renderRedirect2 = () => {
    if (this.state.redirect2) {
      return <Redirect to='/PC/:daterID' />
    }
  }
  renderRedirect3 = () => {
    if (this.state.redirect3) {
      return <Redirect to='/PC/:daterID/update_profile' />
    }
  }
  renderRedirect4 = () => {
    if (this.state.redirect4) {
      return <Redirect to='/PC/:daterID/my_account' />
    }
  }
  renderRedirect5 = () => {
    if (this.state.redirect5) {
      return <Redirect to='/PC/:daterID/update_account' />
    }
  }
  renderRedirect6 = () => {
    if (this.state.redirect6) {
      return <Redirect to='/PC/:daterID/search' />
    }
  }
  renderRedirect7 = () => {
    if (this.state.redirect7) {
      return <Redirect to='/login' />
    }
  }

  render(){


  return (
    <div> 
      <head>
        <link href="https://fonts.googleapis.com/css?family=Tangerine&display=swap" rel="stylesheet"></link>
      </head>
      <div align='center' className='title-bar'>

        <h1 className='title'><b>PET CONNECTIONS</b></h1>
      </div>
      <div align='center' className='Nav-bar-bottom'>
      <hr  className = 'Hr-bottom'/>
      {this.renderRedirect1()}
        <button onClick={this.setRedirect1} >Messages</button>
        <select className='Profile-button' >
          {this.renderRedirect2}
          <option onSelect={this.setRedirect2}>Profile</option>
          {this.renderRedirect3}
          <option onSelect={this.setRedirect3}>Update Profile</option></select>
        <select className='Account-button'>
          {this.renderRedirect4}
          <option onSelect={this.setRedirect4}>Account</option>
          {this.renderRedirect5}
          <option onSelect={this.setRedirect5}>Update Account</option>
          </select>
        {this.renderRedirect6}
        <button onClick={this.setRedirect6}>Search</button>
        {this.renderRedirect7}
        <button className='navbar-logout' onClick={this.setRedirect7} align='right'>LogOut</button>   
        <hr className='Hr-bottom' />

      </div>
      <div>
      <BrowserRouter>
      <div>
        <Route exact path="/" component={HomePage} />
        <Route exact path="/add-user" component={Add_User} />
        <Route exact path="/login" component={Login}/>
        <Route exact path="/PC/:daterID" component={Profile} />
        <Route exact path = "/PC/:daterID/search" component={Search}/>
        <Route exact path = "/PC/:daterID/my-account" component={My_Account} />
        <Route exact path = "/PC/:daterID/potential_card" component={Potential_Card} />
      </div>
        </BrowserRouter>
      </div>
    </div>
  );
}
}

export default App;
