import React ,{Component} from 'react';
import logo from './logo.svg';
import { BrowserRouter, Route } from 'react-router-dom';
import './App.css';
import HomePage from './pages/HomePage.js'
import Add_User from './pages/Add_User.js'
import Login from './pages/Login'
import Profile from './pages/Profile'
import Search from './pages/Search'
import My_Account from './pages/My_Account'
import Potential_Card from './components/Potential_card'
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {
  state ={
      redirect: false
  }

  setRedirect = () => {
    this.setState({
      redirect: true
    })
  }

  renderRedirect1 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/messages' />

    }
  }
  renderRedirect2 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID' />
    }
  }
  renderRedirect3 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/update_profile' />
    }
  }
  renderRedirect4 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/my_account' />
    }
  }
  renderRedirect5 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/update_account' />
    }
  }
  renderRedirect5 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/search' />
    }
  }
  renderRedirect5 = () => {
    if (this.state.redirect) {
      return <Redirect to='/login' />
    }
  }

  render(){


  return (
    <div> 
      <head>
        <link href="https://fonts.googleapis.com/css?family=Tangerine&display=swap" rel="stylesheet"></link>
      </head>
      <div align='center' className='title-bar'>

        <h1 className='title'><b>PET CONNECTIONS</b></h1>
      </div>
      <div align='center' className='Nav-bar-bottom'>
      <hr  className = 'Hr-bottom'/>
        <button a href='#'>Messages</button>
        <select a href='#' className='Profile-button' ><option>Profile</option><option>Update Profile</option></select>
        <select a href='#' className='Account-button'><option>Account</option><option>Update Account</option></select>
        <button a href='#'>Search</button>
        <button className='navbar-logout' a href='#' align='right'>LogOut</button>   
        <hr className='Hr-bottom' />

      </div>
      <div>
      <BrowserRouter>
      <div>
        <Route exact path="/" component={HomePage} />
        <Route exact path="/add-user" component={Add_User} />
        <Route exact path="/login" component={Login}/>
        <Route exact path="/PC/:daterID" component={Profile} />
        <Route exact path = "/PC/:daterID/search" component={Search}/>
        <Route exact path = "/PC/:daterID/my-account" component={My_Account} />
        <Route exact path = "/PC/:daterID/potential_card" component={Potential_Card} />
      </div>
        </BrowserRouter>
      </div>
    </div>
  );
}
}

export default App;
