    this.setState({
      redirect: true
    })
  }

  renderRedirect1 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/messages' />
    }
    this.setState({redirect:false}) 
  }
  renderRedirect2 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID' />
    }
    this.setState({redirect:false})
  }
  renderRedirect3 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/update_profile' />
    }
    this.setState({redirect:false})
  }
  renderRedirect4 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/my_account' />
    }
    this.setState({redirect:false})
  }
  renderRedirect5 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/update_account' />
    }
    this.setState({redirect:false})
  }
  renderRedirect6 = () => {
    if (this.state.redirect) {
      return <Redirect to='/PC/:daterID/search' />
    }
    this.setState({redirect:false})
  }
  renderRedirect7 = () => {
    if (this.state.redirect) {
      return <Redirect to='/login' />
    }
    this.setState({redirect:false})
  }

  render(){


  return (
    <div> 
      <head>
        <link href="https://fonts.googleapis.com/css?family=Tangerine&display=swap" rel="stylesheet"></link>
      </head>
      <div align='center' className='title-bar'>

        <h1 className='title'><b>PET CONNECTIONS</b></h1>
      </div>
      <div align='center' className='Nav-bar-bottom'>
      <hr  className = 'Hr-bottom'/>
      {this.renderRedirect1()}
        <button onClick={this.setRedirect} >Messages</button>
        <select className='Profile-button' >
          {this.renderRedirect2}
          <option onSelect={this.setRedirect}>Profile</option>
          {this.renderRedirect3}
          <option onSelect={this.setRedirect}>Update Profile</option></select>
        <select className='Account-button'>
          {this.renderRedirect4}
          <option onSelect={this.setRedirect}>Account</option>
          {this.renderRedirect5}
          <option onSelect={this.setRedirect}>Update Account</option>
          </select>
        {this.renderRedirect6}
        <button onClick={this.setRedirect}>Search</button>
        {this.renderRedirect7}
        <button className='navbar-logout' onClick={this.setRedirect} align='right'>LogOut</button>   
        <hr className='Hr-bottom' />

      </div>
      <div>
      <BrowserRouter>
      <div>
        <Route exact path="/" component={HomePage} />
        <Route exact path="/add-user" component={Add_User} />
        <Route exact path="/login" component={Login}/>
        <Route exact path="/PC/:daterID" component={Profile} />
        <Route exact path = "/PC/:daterID/search" component={Search}/>
        <Route exact path = "/PC/:daterID/my-account" component={My_Account} />
        <Route exact path = "/PC/:daterID/potential_card" component={Potential_Card} />
      </div>
        </BrowserRouter>
      </div>
    </div>
  );
}
}

export default App;
