import React, {Component} from 'react'
import PCAPI from '../api/PCAPI'

class Potential_Card extends Component{
  state = {
    dater: {},
    redirect: false
  }
componentDidMount() {
  const id = this.props.match.params.daterID
  PCAPI.fetchDaterByID(id)
    .then((dater) => this.setState({
      dater: dater
  }))
}
render(){
  const dater = this.state.dater
  const redirct = this.state
  return(
    <div className='potentialCard' >
      <h1 className='potentialCardUserName'><b>User Name</b></h1>
      <div className='potentialCardDaterPhoto'>
        <img src= {dater['dater_photo']} alt='dater_photo' width='400' height='400' />
      </div>
      <h3 align='center'>City: {dater['city']}</h3>
      <h3 align='center' >State: {dater['state']}</h3>
      <h3 align='center' >Age: {dater['age']}</h3>

    </div>

  )
}
}

export default Potential_Card
